from rest_framework import serializersfrom .models import Videoclass VideoSerializer(serializers.HyperlinkedModelSerializer):    class Meta:        model = Video        fields = ('id','name','poster')class VideoDetailSerializer(serializers.HyperlinkedModelSerializer):    class Meta:        model = Video        fields = ('name','poster','url','classify','click_count','desc','cost_coin','putaway_time')