INSERT INTO `base_video` VALUES ('1', '线性代数的本质＃5：三维线性变换', '', '', 0, 0, '三维的线性变换看起来像什么？在先前我们已经谈过矩阵与线性变换之间的关系，而这部影片我们会将同样的概念扩张到三维的情况。', 0, '2019-07-01 06:37:25.648195', NULL);
INSERT INTO `base_video` VALUES ('2', '微积分的本质＃3：用几何求得导数公式', '', '', 0, 0, '以几何的直觉推导一些求导数公式，像求幂规则和sine的导数看一下Brilliant:', 0, '2019-07-03 09:13:32.917379', NULL);
